modelBuilder.elements:
root -> {
    nodes = $root.traverse(
       descend: node ->  { $node.get(`fma:ComponentNode#children`) }, 
       compose: result -> childResults -> node -> parent -> { 
            $node.get(`fma:ComponentNode#component`).instanceOf(`fma:SinglePart`) ?  concat($node,$childResults.flatten()) : $childResults.flatten()
       }
    );
   nodesByComponent = $nodes.groupBy(node -> $node.get(`fma:ComponentNode#component`));
    $nodesByComponent
     .map( entry -> 
         new(`fma:PartsListEntry`,transient: true)
           ..set(`fma:PartsListEntry#part`,$entry.getKey())
           ..set(`fma:PartsListEntry#connections`,$entry.getValue())
     )
}
