arguments.data:
rows -> 
{
    revision = $rows.isEmpty()? currentRevision(): $rows.elementAt(0).get(`fma:PartsListEntry#part`).revision();
    materials = all(`fma:Material`).inRevision($revision);
{
	'type': 'bar',
	'data': {
	    'labels':  $materials ,
		'datasets': [
		    {
		        'label': 'Kosten pro Material',
			    'data': 
			    $materials.map(material -> $rows.filter(
			        row -> $row.get(`fma:PartsListEntry#part`).get(`fma:SinglePart#material`).containsElement($material)
			              )
			         )     
			        .map(row -> $row.get(`fma:PartsListEntry#costs`).sum() )
		    }
		]
	},
	'options': {
		'maintainAspectRatio': false,
		'plugins': {
            'colorschemes': {
            	// See https://nagix.github.io/chartjs-plugin-colorschemes/colorchart.html
                'scheme': 'tableau.Tableau10'
            }
        },
		'scales': {
		    'y': {
			    'ticks': {
			        'min': 0
			    }
		    },
			'x': {
				'gridLines': {
					'offsetGridLines': true
				}
			}
		}
	}
}
}
