modelBuilder.children:
node -> model -> {
parts = switch{
$node.instanceOf(`synchra.Bom:Composition`): $node.get(`synchra.Bom:Composition#parts`);
$node.instanceOf(`synchra.Bom:ConstructionPart`):$node.get(`synchra.Bom:ConstructionPart#implementedBy`).get(`synchra.Bom:Composition#parts`);
default: null;
};
config = $model.get(`synchra.Bom:DerivedComposition#classifiers`);

switch {
$node == $model: $node.get(`synchra.Bom:DerivedComposition#master`);
default: $parts.filter(p->{
cond = $p.get(`synchra.Bom:ConstructionPart#codeRule`);
condType = $cond.get(`synchra.Bom:ConstructionCondition#type`);
condClassifiers = $cond.get(`synchra.Bom:ConstructionCondition#classifiers`);
switch{
$cond == null: true;
$condType == `synchra.Bom:ConditionType#AND`: $config.containsAll($condClassifiers);
$condType == `synchra.Bom:ConditionType#OR`: $config.containsSome($condClassifiers);
$condType == `synchra.Bom:ConditionType#NOT`: !$config.containsSome($condClassifiers);
default: true;
}
});
};

}
